package com.orange.androidskeleton.ui.postsimport androidx.lifecycle.*import com.orange.androidskeleton.base.BaseViewModelimport com.orange.androidskeleton.repository.RedditPostRepositoryimport com.orange.androidskeleton.vo.RedditPostimport com.orange.androidskeleton.vo.Resourceimport io.reactivex.rxkotlin.subscribeByimport javax.inject.Injectclass PostsViewModel@Inject constructor(redditPostRepository: RedditPostRepository) : BaseViewModel() {    val users: MutableLiveData<Resource<List<RedditPost>>>  = MutableLiveData()    init {        disposable.add(redditPostRepository            .loadPosts(DEFAULT_SUBREDDIT)            .subscribeBy (                onNext = { users.value = it }            ))    }    companion object {        const val DEFAULT_SUBREDDIT = "jokes"    }}